import './App.css';
import { useState } from 'react';
import { createProduct, updateProduct, deleteProduct, getProducts } from './http';
import { Delete, Edit, Cancel, CheckCircle } from '@material-ui/icons'; 

function App() {
  const [name, setName] = useState('');
  const [price, setPrice] = useState('');
  const [productList, setProductList] = useState([]);
  const [selected, setSelected] = useState('');
  const [id, setId] = useState('');
  // eslint-disable-next-line
  const load = useState(listProducts);
  const [message, setMessage] = useState('');
  const [search, setSearch] = useState('');

  async function handleSubmit(event) {
    event.preventDefault();
    const data = {
      name: name,
      price: price
    }
    if (selected) {
      await updateProduct(id, data);
      listProducts();
      setMessage(" Produto atualizado com sucesso!");
    } else {
      await createProduct(data);
      clearFields();
      listProducts();
      setMessage(" Produto registrado com sucesso!");
    }
  }

  async function handleDelete(id) {
    await deleteProduct(id);
    clearFields();
    listProducts();
    setMessage(" Produto excluído com sucesso!");
  }

  async function listProducts() {
    const response = await getProducts();
    if (response) {
      setProductList(response.data);
    } else {
      setMessage(" Não há produtos para serem exibidos, que tal registrar um primeiro?");
    }
  }

  function clearFields() {
    setId('');
    setName('');
    setPrice('');
    setSelected('');
    setMessage('');
  }
  function clearMessage() {
    setMessage('');
  }
  function Message() {
    if (message) return (<div className="message" onClick={clearMessage} ><Cancel /> {message}</div>);
    else return null;
  }
  function List() {
    if (productList.length >= 1) {
      if (search) {
        let term = search.toUpperCase();
        productList.map((val ,key) => {
        for(let item of term){
          if(term[item] == val.name[item].toUpperCase()){
            return (
              <div className="product">
                <div className="product-info">
                  {val.name}
                </div>
                <div>R${parseFloat(val.price).toFixed(2)}
                </div>
                <span onClick={() => {
                  setName(val.name);
                  setPrice(val.price);
                  setId(val.id);
                  setSelected(val);
                }}>
                  <Edit className="edit-button" />
                </span>

                <span onClick={() => {
                  setSelected(val);
                  handleDelete(val.id);
                }}>
                  <Delete className="delete-button" />
                </span>
              </div>);}else return null;
              }}); 
      } else {
      return productList.map((val, key) => {
        return (
          <div className="product">
            <div className="product-info">
              {val.name}
            </div>
            <div>R${parseFloat(val.price).toFixed(2)}
            </div>
            <span onClick={() => {
              setName(val.name);
              setPrice(val.price);
              setId(val.id);
              setSelected(val);
            }}>
              <Edit className="edit-button" />
            </span>

            <span onClick={() => {
              setSelected(val);
              handleDelete(val.id);
            }}>
              <Delete className="delete-button" />
            </span>
          </div>);
      })}
    } else {
      return null;
    }
  }

  return (
    <div className="App">
      <h1>Doceria_<span>Feliz</span></h1>
      <Message />
      <form className="info" onSubmit={(event) => { handleSubmit(event); }}>
        <div>
          <label>Nome:
          <input
              type="text"
              onChange={(event) => {
                setName(event.target.value);
              }} value={name} required>
            </input>
          </label>
        </div>
        <div>
          <label>Preço: R$
          <input
              className="campo-preco"
              type="number"
              onChange={(event) => {
                setPrice(event.target.value);
              }} value={price} required>
            </input>
          </label>
        </div>
        <div>
          <span onClick={(event) => { handleSubmit(event); }}><CheckCircle /></span>
          <span onClick={clearFields}><Cancel /></span>
        </div>
        <div>
          <input
            className="campo-busca"
            type="text"
            placeholder="Filtrar..."
            onChange={(event) =>{
              setSearch(event.target.value);
            }}>
          </input>
        </div>
      </form>
      <div className="list">
        <List />
      </div>
    </div>
  );

}

export default App;
