import './App.css';
import { useState } from 'react';
import { createProduct, updateProduct, deleteProduct, getProducts } from './http';
import { Delete, Edit, Cancel, Done} from '@material-ui/icons';

function App() {

  const [name, setName] = useState('');
  const [price, setPrice] = useState('0');
  const [productList, setProductList] = useState([]);
  const [selected, setSelected] = useState('');
  const [id, setId] = useState('');

  let load = 0;

  async function handleSubmit(event) {
    event.preventDefault();
    const data = {
      name: name,
      price: price
    }

    if (selected) {
      await updateProduct(id, data);
      listProducts();
    } else {
      await createProduct(data);
      clearFields();
      listProducts();
    }
  }
  async function handleDelete(id) {
    const response = await deleteProduct(id);
    console.log(response);
    clearFields();
    listProducts();
  }
  async function listProducts() {
    const response = await getProducts();
    setProductList(response.data);
  }
  function clearFields() {
    setId('');
    setName('');
    setPrice('0');
    setSelected('');
  }
  function List() {
    if (load == 0){
    listProducts();
    load++;
  }
    return null;
  }

  return (
    <div className="App">
      <form className="info" onSubmit={(event) => { handleSubmit(event);}}>
        <label>Nome:
          <input
            type="text"
            onChange={(event) => {
              setName(event.target.value);
            }} value={name} required>
          </input>
        </label>
        <label>Preço:
          <input
            type="number"
            onChange={(event) => {
              setPrice(event.target.value);
            }} value={price} required>
          </input>
        </label>
        <button type="button" onClick={clearFields}><Cancel /></button>
        <button type="submit"><Done /></button>
      </form>

      <List />       

      {productList.map((val, key) => {
        return (<div>
          Nome: {val.name} - Preço unitário: R${val.price}
          <button onClick={() => {
            setName(val.name);
            setPrice(val.price);
            setId(val.id);
            setSelected(val);
          }}><Edit /></button>

          <button onClick={() => {
            setSelected(val);
            handleDelete(val.id);
          }}><Delete /></button>
        </div>)
      })}

    </div>
  );

}

export default App;
