import './App.css';
import { useState } from 'react';
import { createProduct, updateProduct, deleteProduct, getProducts } from './http';

function App() {

  const [name, setName] = useState('');
  const [price, setPrice] = useState('0');
  const [productList, setProductList] = useState([]);
  const [selected, setSelected] = useState('');

  async function handleSubmit(event) {
    event.preventDefault();
    if (selected) {
      await updateProduct(id, data);
    } else {
      data = {
        name: name,
        price: price
      }
      await createProduct(data);
      clearFields();
      listProducts();
    }
  }

  async function handleDelete(id) {
    const response = await deleteProduct(id);
    console.log(response);
    clearFields();
    listProducts();
  }
  async function listProducts() {
    const response = await getProducts();
    setProductList(response.data);
  }
  function clearFields() {
    setName('');
    setPrice('0');
    setSelected('');
  }


  return (
    <div className="App">
      <form className="info" onSubmit={(event) => { handleSubmit(event);}}>
        <label>Nome:
          <input
            type="text"
            onChange={(event) => {
              setName(event.target.value);
            }}>
          </input>
        </label>
        <label>Preço:
          <input
            type="number"
            onChange={(event) => {
              setPrice(event.target.value);
            }}>
          </input>
        </label>

        <button type="submit">Criar</button>
      </form>

      <button type="button" onClick={listProducts}>Mostra</button>

      {listProducts}
      {productList.map((val, key) => {
        return (<div>
          Nome: {val.name} - Preço unitário: R${val.price}
          <button onClick={() => {
            setName(val.name);
            setPrice(val.price);
            setSelected(val);
          }}>Editar</button>
          <button onClick={() => {
            setSelected(val);
            handleDelete(val.id);
          }}>Apagar</button>
        </div>)
      })}

    </div>
  );
}

export default App;
