import './App.css';
import { useState } from 'react';
import { createProduct, updateProduct, deleteProduct, getProducts } from './http';
import { Delete, Edit, Cancel, Done} from '@material-ui/icons';

function App() {

  const [name, setName] = useState('');
  const [price, setPrice] = useState('0');
  const [productList, setProductList] = useState([]);
  const [selected, setSelected] = useState('');
  const [id, setId] = useState('');
  const load = useState(listProducts);

  async function handleSubmit(event) {
    event.preventDefault();
    const data = {
      name: name,
      price: price
    }
    if (selected) {
      await updateProduct(id, data);
      listProducts();
    } else {
      await createProduct(data);
      clearFields();
      listProducts();
    }
  }

  async function handleDelete(id) {
    const response = await deleteProduct(id);
    console.log(response);
    clearFields();
    listProducts();
  }

  async function listProducts() {
    const response = await getProducts();
    setProductList(response.data);
  }

  function clearFields() {
    setId('');
    setName('');
    setPrice('0');
    setSelected('');
  }

  return (
    <div className="App">
      <h1>doceria feliz</h1>
      <form className="info" onSubmit={(event) => { handleSubmit(event);}}>
        <div>
        <label>Nome:
          <input
            type="text"
            onChange={(event) => {
              setName(event.target.value);
            }} value={name} required>
          </input>
        </label>
        </div>
        <div>
        <label>Preço:
          <input
            type="number"
            onChange={(event) => {
              setPrice(event.target.value);
            }} value={price} required>
          </input>
        </label>
        </div>
        <div>
        <button type="button" onClick={clearFields}><Cancel /></button>
        <button type="submit"><Done /></button>
        </div>    
      </form>
      <div className="list">
      {productList.map((val, key) => {
        return (<div className="product"><div className="product-info">
          {val.name}</div><div>Preço unitário: R${val.price}</div>
          <div  onClick={() => {
            setName(val.name);
            setPrice(val.price);
            setId(val.id);
            setSelected(val);
          }}><Edit className="edit-button" /></div>

          <div onClick={() => {
            setSelected(val);
            handleDelete(val.id);
          }}><Delete className="delete-button" /></div>
        </div>)
      })}
      </div>
    </div>
  );

}

export default App;
