import './App.css';
import { useState } from 'react';
import { createProduct, updateProduct, deleteProduct, getProducts} from './http';

function App() {

  const [id, setId] = useState('');
  const [name, setName] = useState('');
  const [price, setPrice] = useState('0');
  const [productList, setProductList] = useState([]);
  const [selected, setSelected] = useState('');

  function handleSubmit(event, id, data) {
    event.preventDefault();

    if (selected) updateProduct(id, data);
    else createProduct(data);
    clearFields();
    listProducts();
  }

  function handleDelete(id) {
    const response = deleteProduct(id);
    console.log(response);
    clearFields();
    listProducts();
  }
  async function listProducts() {
  const response = await getProducts();
  setProductList(response.data);
  }
  function clearFields(){
    setId('');
    setName('');
    setPrice('0');
    setSelected('');
  }


  return (
    <div className="App">
      <form className="info" onSubmit={handleSubmit}>
        <label>Nome:
          <input
            type="text"
            onChange={(event) => {
              setName(event.target.value);
            }}>
          </input>
        </label>
        <label>Preço:
          <input
            type="text"
            onChange={(event) => {
              setPrice(event.target.value);
            }}>
          </input>
        </label>

        <button type="submit">Criar</button>
      </form>

      <button type="button"onClick={listProducts}>Mostra</button>

      {productList.map((val, key) => {
        return (<div>
                Nome: {val.name.capitalize()} - Preço: {val.price}
                </div>)
      })}

    </div>
  );
}

export default App;
